options.code:
totalQty = 0;
inStockQty = 0;
lowInStock = 0;
outOfStock = 0;
yetToReceive = 0;

queries.getProducts.data.forEach((product) => {
  totalQty += Number(product.quantity);
  inStockQty += product.status == "In stock" ? Number(product.quantity) : 0;
  yetToReceive += product.status == "Yet to receive" ? Number(product.quantity) : 0;
  lowInStock += Number(product.quantity) <= 10 && Number(product.quantity) > 0 ? 1 : 0;
  outOfStock += Number(product.quantity) == 0 ? 1 : 0;
});

return {
  quantityInTotal: totalQty,
  quantityInStock: inStockQty,
  quantityYetToReceive: yetToReceive,
  lowInStock: lowInStock,
  outOfStock: outOfStock,
};

